#!/bin/bash

# clean up files generated by initial atap extract job for air temperature
# (job was canceled because file names and extract field names needed to be changed)

for y in /sciclone/data20/aiddata/REU/projects/kfw/extracts/terrestrial_air_temperature/output/*;do 
	year=`echo $y | sed 's/.*\///'`
	if [[ "$year" == "1944" ]];then
		exit 0
	fi 
	for d in "$y"/*;do 
		day=`echo $d | sed 's/.*\///'`
		for f in "$d"/*;do 
			file=`echo "$f" | sed 's/.*\///'`
			if [[ "$file" == temp_extract_"$year"_"$day".* ]];then 
				rm "$f"
			fi
		done
	done
done
